mapper.insert:
INSERT INTO COMMENT(board_id
					, reg_user
					, content
					, dt
					, parent_id)
			VALUES(#{boardId}
				, #{regUser}
				, #{content}
				, NOW()
				, #{parentId});

mapper.update:
      	UPDATE COMMENT 
      	SET content = #{content}
		WHERE id = #{id}

mapper.select:
SELECT id, board_id, reg_user, content, dt, parent_id
FROM COMMENT
WHERE 	board_id = #{boardId}
		AND parent_id = #{parentId}
ORDER BY dt desc

	LIMIT #{start}, #{length}
